package AnujB;

public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=6;
		for(int i=1;i<=num;i++) {
			for(int j=i;j<=num;j++) {
				System.out.print(" ");}
			for(int k=num-i;k<num;k++) {
				System.out.print(k+" ");
				
			}
			
			System.out.println();
				
			
			
			
		}
		}
		
	}


