package AI;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=0; int count;
		if (num==0) {System.out.println(1);}
		else {
		for(int i=num-1;i>0;i--) {
			
			num = num*i;
			
			
		}
		System.out.println(num);}

	}

}
