package javapractice;

public class PowerForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=3; int y=0; 
		int z=x;
		if (y==0) {
			System.out.println(1);
		}else {
		for(int i=1;i<y;i++) {
			
			z = z*x;
		}
		System.out.println(z);}
	}

}
