package javapractice;

public class evaluatepower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Code to evaluate x power y
		int x=3,y=0;
		int z=x;
		for(int c=1;c<y;c++)
		{
			z=z*x;
		}
		System.out.println(z);//What would be output?
	}

}
